(113, 'Bangladesh - South Africa ODI Recharge Offer', 'Bangladesh-South-Africa-ODI-Recharge-Offer', '<p style=\"text-align:left\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,serif\">On Bangladesh vs South Africa ODI series, AKASH brings a special cashback offer!</span></span></p>\r\n\r\n<p style=\"text-align:left\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,serif\">Anybody recharging BDT 1,500 on the match day will receive a cashback equal to TOTAL RUNS scored by Bangladesh in that Match.</span></span></p>\r\n\r\n<p style=\"text-align:left\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,serif\"><strong>Campaign Duration:</strong></span><span style=\"font-family:Calibri,serif\"> 18, 20 & 23 March 2022</span></span></p>\r\n\r\n<p style=\"text-align:left\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,serif\"><strong>Terms & Conditions:</strong></span></span></p>\r\n\r\n<ol>\r\n	<li>\r\n	<p style=\"text-align:left\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,serif\">Customer must recharge </span><span style=\"font-family:Calibri,serif\"><strong>exactly BDT 1,500 </strong></span><span style=\"font-family:Calibri,serif\">in his AKASH account on the Bangladesh VS South Africa ODI match day.</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:left\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,serif\">Customer needs to recharge </span><span style=\"font-family:Calibri,serif\"><strong>within the Match-day</strong></span><span style=\"font-family:Calibri,serif\"> for that day’s cashback.</span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px; text-align:left\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,serif\"><strong>(From 12:00 AM to 11:59 PM)</strong></span></span></p>\r\n\r\n<ol start=\"3\">\r\n	<li>\r\n	<p style=\"text-align:left\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,serif\">Cashback amount will be equal to the total runs scored by Bangladesh on that day. </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:left\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,serif\">Cashback will be given to your AKASH account within 5 working days after the match.</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:left\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,serif\">A customer can recharge as many times as he/she wants. For every recharge of BDT 1,500, he/she will be entitled for the cashback</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:left\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,serif\">This offer will be applicable on top of all other offers applicable for the customer.</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:left\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,serif\">AKASH reserves all rights to change, extend and cancel the offer at any time.</span></span></p>\r\n	</li>\r\n</ol>\r\n', '1647525871-54275841.jpg', '1647525871-6924780.jpg', NULL, 'Offer', 0, 0, 1, '2022-03-17 20:55:08', '2022-03-23 23:59:08', '2022-03-17 20:19:45', '2022-03-17 20:04:31', ''),
(114, 'AKASH PACKAGES WITH MORE CHANNELS!', 'akash-package-with-more-channels', '<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">You can now enjoy a higher number of channels with all AKASH packages.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">AKASH Standard will now be available with 125+ channels, including 40+ HD channels, for BDT 400. Customers will now be able to enjoy all channels in AKASH bouquet by subscribing to AKASH Standard. </span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">AKASH Lite Plus will now be available with 95+ channels, including 26+ HD channels, for BDT 300.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">AKASH Lite will now be available with 75 channels, including 20 HD channels, for BDT 250.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">These prices will be effective from 23<sup>rd</sup> March, 12:00AM. </span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Add-ons:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Infotainment Pack-2 Add-on will no longer be available from 23<sup>rd</sup> March, 12:00AM. Customers will no longer be able to add Infotainment Pack-2 to their bouquet. If customers want the channels previously available with Infotainment Pack-2, they can add Infotainment Add-on, which comes with 13 channels for BDT 45.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Terms and Conditions:</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">If a customer&rsquo;s pack is already active, they can enjoy their subscription till the end of their validity for no extra recharge.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Customers have to pay the new subscription fees after their current validity has ended.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Customers who had Infotainment Pack-2 active, and had validity till after 23<sup>rd</sup> March, will receive a cashback to their account.</span></span></li>\r\n</ul>\r\n', '1648122790-47261578.jpg', '1648122791-75956621.jpg', NULL, 'Product', 0, 0, 1, '2022-03-24 17:50:41', '2022-03-29 23:55:41', '2022-03-24 17:53:11', '2022-03-24 17:53:11', ''),
(115, 'Eid Ul Fitr Campaign', 'eid-ul-fitr-discount', '<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">On the occasion Eid Ul Fitr AKASH has launched a campaign where customer can purchase new AKASH connection from retail, website, partner store at <strong>BDT 500 discount</strong>. The campaign period price will be <strong>BDT 3,499</strong> for AKASH Basic connection. In addition to this discount a customer who buys AKASH connection, activates the connection and recharges for 12 months within campaign period would get <strong>benefit up to BDT 1,700</strong>. The amount of the benefit (cashback + <strong>LDP-Long Duration Plan</strong> subscription) is dependent on the package taken.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\">Exclusive Savings on new AKASH connection by EBL Visa Card.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Campaign Timeline:</strong> 1 April – 3 May, 2022</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Terms & Conditions:</strong></span></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Arial,sans-serif\">Customers have to purchase AKASH connection from AKASH retail points or AKASH website within the campaign period.</span></span></li>\r\n	<li><span style=\"font-size:18px\">500tk net discount on the MRP for AKASH Basic connection. The campaign period price will be BDT 3,499 for AKASH Basic connection. </span></li>\r\n	<li><span style=\"font-size:18px\">The discount offer is not available on the STB rather on the whole connection (CPE).</span></li>\r\n	<li><span style=\"font-size:18px\">Strategic partners will have their own campaign. For single CPE purchase through strategic partner this offer will be applicable if they 