(1, 'AKASH Regular Set Top Box', 'Regular', 2999, 'set-top-box', '<p>Free Installation!</p>\n', '<p>With great picture quality, incredibly vivid colours, sharper images and stunning surround sound, AKASH Set Top Box takes TV viewing to greater heights with its wide array of features that ensure an enhanced viewing experience.</p>\n\n<p>Avail AKASH Regular set top box today and enjoy free installation.</p>\n', '1603345333-4363654.jpg', '1603345333-57408225.png', '1603345333-48992572.png', '', '', 0, 0, 'Package', 1, 0, -1, 195, '2020-10-22 16:48:39', '2019-07-21 13:18:44'),
(2, 'AKASH Standard', 'Regular', 5499, 'standard', '<p>Free Installation </p>\n', '<p>With great picture quality, incredibly vivid colours, sharper images and stunning surround sound, AKASH takes TV viewing to greater heights with its wide array of features that ensure an enhanced viewing experience.</p>\n\n<p>Avail AKASH today and enjoy free installation.</p>\n\n<p>Items included: Set-top-box, Remote Control unit, Power adapter, LNB, HDMI Cable, AV Cable, HDMI, AV Out, LNB port, Dish.</p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"color:black\"> *10 meter cable will be free with Full Combo Box, BDT 30 /meter will be applicable as cable charge after 10 meter</span></strong></span></span></p>\n', '1563867229-33183768.png', '1563867229-14635958.png', '1563867229-71745147.png', '1563867229-4363808.png', '1563867229-29408945.png', 0, 0, 'Package', 1, 0, -1, 11, '2020-06-28 00:37:28', '2019-07-21 13:22:13'),
(3, 'AKASH Standard', 'Regular', 5880, 'standard-12-emi', '<p>12 Months EMI @BDT 490/Month</p>\n', '<p>Buy AKASH at a<span style=\"font-size:16px\"><span style=\"color:#e74c3c; font-size:20px\"><strong> monthly installment of BDT 490 for 12 months</strong></span></span> with free installation.</p>\n\n<p>Items included: Set-top-box, Remote Control unit, Power adapter, LNB, HDMI Cable, AV Cable, HDMI, AV Out, LNB port, Dish.</p>\n\n<p><strong> *10 meter cable will be free with Full Combo Box, BDT 30 /meter will be applicable as cable charge after 10 meter</strong></p>\n', '1570090678-59987563.jpg', '1569221920-33084232.png', '1564294644-45510356.png', '1564294644-24430964.png', '1564294644-32563814.png', 0, 490, 'EMI', 1, 0, -1, 9, '2020-06-28 00:37:41', '2019-07-21 13:23:41'),
(4, 'AKASH Standard + 6 Months Subscription', 'Regular', 8070, 'standard-6-subscription-emi', '<p>6 Months EMI @1345/Month</p>\n\n<p>1 Month Free Subscription</p>\n', '<p>Buy AKASH with 6 months\' subscription at a <span style=\"color:#e74c3c\"><span style=\"font-size:20px\"><strong>monthly installment of BDT 1,345 for 6 months</strong></span></span>.</p>\n\n<p>Enjoy free subscription for 1 month with free installation.</p>\n\n<p>Items included: Set-top-box, Remote Control unit, Power adapter, LNB, HDMI Cable, AV Cable, HDMI, AV Out, LNB port, Dish.</p>\n\n<p><strong> *10 meter cable will be free with Full Combo Box, BDT 30 /meter will be applicable as cable charge after 10 meter</strong></p>\n', '1570348740-1628831.png', '1569218205-20725073.png', '1564294697-19061291.png', '1564294697-27721615.png', '1564294697-41900206.png', 1345, 0, 'EMI', 1, 0, -1, 6, '2020-06-28 00:38:17', '2019-07-21 13:25:11'),
(5, 'AKASH Standard + 12 Months Subscription', 'Regular', 11160, 'standard-12-subscription-emi', '<p>12 Months EMI @BDT 930/Month</p>\n\n<p>2 Months Free Subscription</p>\n', '<p>Buy AKASH with 12 months\' subscription at a <span style=\"color:#e74c3c\"><span style=\"font-size:20px\"><strong>monthly installment of BDT 930 for 12 months</strong></span></span>. </p>\n\n<p>Enjoy free subscription for 2 months with free installation.</p>\n\n<p>Items included: Set-top-box, Remote Control unit, Power adapter, LNB, HDMI Cable, AV Cable, HDMI, AV Out, LNB port, Dish.</p>\n\n<p><strong> *10 meter cable will be free with Full Combo Box, BDT 30 /meter will be applicable as cable charge after 10 meter</strong></p>\n\n<p> </p>\n', '1570348699-50363207.png', '1569218093-42501506.png', '1564294730-31391928.png', '1564294730-22840759.png', '1564294730-74183742.png', 0, 930, 'EMI', 1, 0, -1, 8, '2020-06-28 00:37:54', '2019-07-21 13:28:23'),
(6, 'AKASH Family Bundle 1', 'Regular', 9999, 'family-bundle-1', '<p>Installation & 2 Months Free Subscription</p>\n', '<p>Get dual AKASH connection for your family and save BDT 2,999</p>\n', '1563867299-71447568.png', '', '', '', '', 0, 0, 'Offer', 1, 0, -1, 0, '2019-09-18 13:11:22', '2019-07-21 13:34:12'),
