(9, 'AKASH Standard + 6 Months Subscription', 'Regular', 7893, 'standard-6-months-subscription', '<p>Free Installation & 1 Month Subscription</p>\n', '<p>Buy AKASH with 6 months’ subscription and enjoy <strong><span style=\"color:#e74c3c; font-size:20px\">free subscription for 1 month</span></strong> with free installation.</p>\n\n<p>Items included: Set-top-box, Remote Control unit, Power adapter, LNB, HDMI Cable, AV Cable, HDMI, AV Out, LNB port, Dish.</p>\n\n<p><strong> *10 meter cable will be free with Full Combo Box, BDT 30 /meter will be applicable as cable charge after 10 meter</strong></p>\n', '1571121075-1087983.jpg', '1564294582-84824688.png', '1564294582-74298551.png', '1564294582-39307569.png', '1564294582-2300788.png', 0, 0, 'Offer', 1, 0, -1, 5, '2020-06-28 00:38:28', '2019-07-25 13:09:25'),
(10, 'AKASH Standard + 12 Months Subscription', 'Regular', 10287, 'standard-12-months-subscription', '<p>Free Installation & 2 Months Subscription</p>\n', '<p>Buy AKASH with 12 months’ subscription and enjoy <strong><span style=\"color:#e74c3c; font-size:20px\">free subscription for 2 months </span></strong>with free installation.</p>\n\n<p>Items included: Set-top-box, Remote Control unit, Power adapter, LNB, HDMI Cable, AV Cable, HDMI, AV Out, LNB port, Dish.</p>\n\n<p><strong> *10 meter cable will be free with Full Combo Box, BDT 30 /meter will be applicable as cable charge after 10 meter</strong></p>\n', '1571121108-72176561.jpg', '1564294514-98987476.png', '1564294514-91132513.png', '1564294514-83434504.png', '1564294514-58097200.png', 0, 0, 'Offer', 1, 0, -1, 4, '2020-06-28 00:38:48', '2019-07-25 13:11:51'),
(11, 'AKASH Standard', 'Regular', 5640, 'standard-6-emi', '<p>6 Months EMI @940/Month</p>\n', '<p>Buy AKASH<strong> </strong>at a <span style=\"color:#e74c3c; font-size:20px\"><strong>monthly installment of BDT 940 for 6 months</strong></span> with free installation.</p>\n\n<p>Items included: Set-top-box, Remote Control unit, Power adapter, LNB, HDMI Cable, AV Cable, HDMI, AV Out, LNB port, Dish.</p>\n\n<p><strong> *10 meter cable will be free with Full Combo Box, BDT 30 /meter will be applicable as cable charge after 10 meter</strong></p>\n', '1570090716-82926806.jpg', '1569221652-91135188.png', '1569221652-86719804.png', '1569221652-86534329.png', '1569221652-46601091.png', 940, 0, 'EMI', 1, 0, -1, 7, '2020-06-28 00:38:07', '2019-09-23 12:54:12'),
(12, 'AKASH Multi-TV (2 Connection)', 'Regular', 9298, 'multi-tv-2', '<p>Save 12% on Monthly Subscription Fee !!!</p>\n', '<p>Now all family members can enjoy their favorite TV programs in their own room. With AKASH Multi TV Connection you’ll able to save significant amount of money both on connection and monthly subscription. Subscribers will be able to save up to 12% on the total monthly subscription of 2 connections.</p>\n', '1580983606-10045867.jpg', '1580986214-7068401.png', '1580986214-8187086.png', '1580986214-87409414.png', NULL, 0, 0, 'Package', 1, 0, -1, 0, '2020-06-28 00:39:00', '2020-02-06 16:06:46'),
(13, 'AKASH Multi-TV (2 Connection)', 'Regular', 9716, 'multi-tv-2-emi-6', '<p>6 Months EMI @BDT 1619/Month</p>\n', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">Buy AKASH Multi-TV<strong> </strong>at a </span><strong><span style=\"font-size:15.0pt\"><span style=\"color:#e74c3c\">monthly installment of BDT 1619 for 6 months</span></span></strong><span style=\"color:black\"> with free installation.</span></span></span></p>\n', '1580983938-53835700.jpg', '1580986280-45216803.png', '1580986280-58702972.png', '1580986280-18470181.png', NULL, 1619, 0, 'EMI', 1, 0, -1, 0, '2020-06-28 00:39:12', '2020-02-06 16:12:18'),
(14, 'AKASH Multi-TV (2 Connection)', 'Regular', 10135, 'multi-tv-2-emi-12', '<p>12 Months EMI @BDT 845/Month</p>\n', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">Buy AKASH Multi-TV<strong> </strong>at a </span><strong><span style=\"font-size:15.0pt\"><span style=\"color:#e74c3c\">monthly installment of BDT 845 for 12 months</span></span></strong><span style=\"color:black\"> with free installation.</span></span></span></p>\n', '1580984190-72633222.jpg', '1580986398-72868736.png', '1580986398-81307745.png', '1580986398-17361510.png', NULL, 0, 845, 'EMI', 1, 0, -1, 0, '2020-06-28 00:39:24', '2020-02-06 16:16:30'),
(15, 'AKASH Multi-TV (3 Connection)', 'Regular', 13297, 'multi-tv-3', '<p>Save 25% on Monthly Subscription Fee !!!</p>\n', '<p>Now all family members can enjoy their favorite TV programs in their own room. With AKASH Multi TV Connection you’ll able to save significant amount of money both on connection and monthly subscription. Subscribers will be able to save up to 25% on the total monthly subscription of 3 connections.</p>\n', '1580984282-4206965.jpg', '1580986454-94327408.png', '1580986454-38466294.png', '1580986454-38992047.png', NULL, 0, 0, 'Package', 1, 0, -1, 0, '2020-06-28 00:39:41', '2020-02-06 16:18:02'),
(16, 'AKASH Multi-TV (3 Connection)', 'Regular', 13895, 'multi-tv-3-emi-6', '<p>6 Months EMI @BDT 2316/Month</p>\n', '<p><span style=\"font-size:11.0pt\"><span style=\"font-family:\"Calibri\",\"sans-serif\"\"><span style=\"color:black\">Buy AKASH Multi-TV<strong> </strong>at a </span></span></span><strong><span style=\"font-size:15.0pt\"><span style=\"font-family:\"Calibri\",\"sans-serif\"\"><span style=\"color:#e74c3c\">monthly installment of BDT 2316 for 6 months</span></span></span></strong><span style=\"font-size:11.0pt\"><span style=\"font-family:\"Calibri\",\"sans-serif\"\"><span style=\"color:black\"> with free installation.</span></span></span></p>\n', '1580984378-57939110.jpg', '1580986496-65128765.png', '1580986496-73067600.png', '1580986496-30964067.png', NULL, 2316, 0, 'EMI', 1, 0, -1, 0, '2020-06-28 00:39:55', '2020-02-06 16:19:38'),
